// Define the secret name
function secretsNames() {
  const secretsNames = process.env.AWS_SECRETS_NAMES

  if (!secretsNames) {
    throw new Error('Missing environment variable: AWS_SECRETS_NAMES')
  }
  // Remove any enclosing square brackets and quotes from the string
  const cleanList = process.env.AWS_SECRETS_NAMES.replace(/[[\]"]/g, '')
  // Split the string by commas to get an array of values
  return cleanList.split(',').map((item) => item.trim())
}

module.exports = {
  secretsNames,
}
